// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "./Dapptoken.sol";
import "./Daitoken.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
contract Farming {
    string public name = "Farming";
    address public owner;
    ERC20 public dappToken;
    ERC20 public daiToken;

    uint256 public rewardRate = 100;
    uint256 public lastUpdateTime;
    uint256 public rewardPerTokenStored;

    mapping(address => uint256) public userRewardPerTokenPaid;
    mapping(address => uint256) public rewards;

    uint public totalSupply;
    mapping(address => uint256) public Stakingbalances;

    constructor(address _daiToken, address _dappToken){
        daiToken = ERC20(_daiToken);
        dappToken = ERC20(_dappToken);
        owner = msg.sender;
    }

    function rewardPerToken() public view returns (uint) {
        if (totalSupply == 0) {
            return 0;
        }
        return
            rewardPerTokenStored +
            (((block.timestamp - lastUpdateTime) * rewardRate * 1e18) / totalSupply);
    }

    function earned(address account) public view returns (uint) {
        return
            ((Stakingbalances[account] *
                (rewardPerToken() - userRewardPerTokenPaid[account])) / 1e18) +
            rewards[account];
    }

    modifier updateReward(address account) {
        rewardPerTokenStored = rewardPerToken();
        lastUpdateTime = block.timestamp;

        rewards[account] = earned(account);
        userRewardPerTokenPaid[account] = rewardPerTokenStored;
        _;
    }
    function stake(uint256 _amount) external updateReward(msg.sender) {
        require(_amount > 0, "amount can be 0");
        daiToken.transferFrom(msg.sender, address(this), _amount);
        Stakingbalances[msg.sender] += _amount;
        totalSupply += _amount;
    }

    function withdraw(uint _amount) external updateReward(msg.sender) {
        totalSupply -= _amount;
        Stakingbalances[msg.sender] -= _amount;
        daiToken.transfer(msg.sender, _amount);
    }

    function getReward() external updateReward(msg.sender) {
        uint reward = rewards[msg.sender];
        rewards[msg.sender] = 0;
        dappToken.transfer(msg.sender, reward);
    }
}
